&#34;); char curr,prev; prev = &#39;\0&#39;; curr = input(); while(prev!=&#39;*&#39;||curr!=&#39;/&#39;) {prev = curr; curr = input();} } &#34;//&#34; { printf(&#34;&lt;COMMENT&gt;
&#34;); char curr; curr = input(); while(curr!=&#39;
&#39;) curr = input(); } &#34;int&#34; {printf(&#34;&lt;INT&gt;
&#34;);} &#34;float&#34; {printf(&#34;&lt;FLOAT&gt;
&#34;);} &#34;void&#34; {printf(&#34;&lt;VOID&gt;
&#34;);} &#34;if&#34; {printf(&#34;&lt;IF&gt;
&#34;);} &#34;else&#34; {printf(&#34;&lt;ELSE&gt;
&#34;);} &#34;while&#34; {printf(&#34;&lt;WHILE&gt;
&#34;);} &#34;break&#34; {printf(&#34;&lt;BREAK&gt;
&#34;);} &#34;return&#34; {printf(&#34;&lt;RETURN&gt;
&#34;);} &#34;+&#34; {printf(&#34;&lt;ADD&gt;
&#34;);} &#34;-&#34; {printf(&#34;&lt;SUB&gt;
&#34;);} &#34;*&#34; {printf(&#34;&lt;MUL&gt;
&#34;);} &#34;/&#34; {printf(&#34;&lt;DIV&gt;
&#34;);} &#34;%&#34; {printf(&#34;&lt;MOD&gt;
&#34;);} &#34;!&#34; {printf(&#34;&lt;NOT&gt;
&#34;);} &#34;,&#34; {printf(&#34;&lt;,&gt;
&#34;);} &#34;=&#34; {printf(&#34;&lt;=&gt;
&#34;);} &#34;;&#34; {printf(&#34;&lt;;&gt;
&#34;);} &#34;&gt;=&#34; {printf(&#34;&lt;GE&gt;
&#34;);} &#34;&lt;=&#34; {printf(&#34;&lt;LE&gt;
&#34;);} &#34;==&#34; {printf(&#34;&lt;EQ&gt;
&#34;);} &#34;!=&#34; {printf(&#34;&lt;NE&gt;
&#34;);} &#34;&gt;&#34; {printf(&#34;&lt;GT&gt;
&#34;);} &#34;&lt;&#34; {printf(&#34;&lt;LT&gt;
&#34;);} &#34;||&#34; {printf(&#34;&lt;OR&gt;
&#34;);} &#34;&amp;&amp;&#34; {printf(&#34;&lt;AND&gt;
&#34;);} &#34;(&#34; {printf(&#34;&lt;(&gt;
&#34;);} &#34;)&#34; {printf(&#34;&lt;)&gt;
&#34;);} &#34;[&#34; {printf(&#34;&lt;[&gt;
&#34;);} &#34;]&#34; {printf(&#34;&lt;]&gt;
&#34;);} &#34;{&#34; {printf(&#34;&lt;{&gt;
&#34;);} &#34;}&#34; {printf(&#34;&lt;}&gt;
&#34;);} 
 { ++yylineno; } &#34;0&#34;[0-9a-zA-Z]* { int pos = 1; char c,buf[2]; c = yytext[pos++]; if (c!=&#39;x&#39;&amp;&amp;c!=&#39;X&#39;){ int total=0; while (c&gt;=&#39;0&#39;&amp;&amp;c&lt;=&#39;7&#39;){ if (c&gt;&#39;9&#39;||c&lt;&#39;0&#39;){ printf(&#34;Error at Line %d: Invalid characters \&#39;%s\&#39;
&#34;, yylineno, yytext); return -1; } buf[0]=c; buf[1]=&#39;\0&#39;; total = total*8+atoi(buf); c = yytext[pos++]; } if (c!=&#39; &#39;&amp;&amp;c!=&#39;	&#39;&amp;&amp;c!=&#39;
&#39;&amp;&amp;c!=&#39;\0&#39;){ printf(&#34;Error at Line %d: Invalid octalnumber \&#39;%s\&#39;
&#34;, yylineno, yytext); return -1; } printf(&#34;&lt;INTNUM, %d&gt;
&#34;, total); return 1; } else { c = yytext[pos++]; int total = 0; while ((c&gt;=&#39;0&#39;&amp;&amp;c&lt;=&#39;9&#39;)||(c&gt;=&#39;a&#39;&amp;&amp;c&lt;=&#39;f&#39;)||(c&gt;=&#39;A&#39;&amp;&amp;c&lt;=&#39;F&#39;)){ if (c&gt;=&#39;0&#39;&amp;&amp;c&lt;=&#39;9&#39;){ total = total*16 + c-&#39;0&#39;; } if (c&gt;=&#39;a&#39;&amp;&amp;c&lt;=&#39;f&#39;){ total = total*16 + c-&#39;a&#39;+10; } if (c&gt;=&#39;A&#39;&amp;&amp;c&lt;=&#39;F&#39;){ total = total*16 + c-&#39;A&#39;+10; } c = yytext[pos++]; } if (c!=&#39; &#39;&amp;&amp;c!=&#39;	&#39;&amp;&amp;c!=&#39;
&#39;&amp;&amp;c!=&#39;\0&#39;){ printf(&#34;Error at Line %d: Invalid hex number \&#39;%s\&#39;
&#34;, yylineno, yytext); return -1; } printf(&#34;&lt;INTNUM, %d&gt;
&#34;, total); } } {identifier} {return IDENTIFIER;} [0-9]+ {printf(&#34;&lt;INTNUM, %d&gt;
&#34;,atoi(yytext));} . { printf(&#34;Error at Line %d: Invalid characters \&#39;%s\&#39;
&#34;, yylineno, yytext); } %% int main(int argc, char** argv) { if (argc &gt; 1) { yyin = fopen(argv[1], &#34;r&#34;); if (!yyin) { perror(&#34;Error opening file&#34;); return 1; } } else { printf(&#34;Usage: %s &lt;filename&gt;
&#34;, argv[0]); return 1; } while (yylex() != 0); fclose(yyin); // Remember to close the file when done return 0; } int yywrap() { return 1; } 总结 本文实现了一个简单的SysY词法分析器，并且将在下一篇文章中利用Bison+FLex结合此词法分析器进行语法分析器的制作。
